mapper.insert:
INSERT INTO tb_role(
    id,     parent_id,      role_desc,      role_name
)
VALUES(
    #{role.id},  #{parentId, jdbcType=VARCHAR}, #{role.description, jdbcType=VARCHAR}, #{role.name}
)

INSERT INTO tb_user_role(
    tb_role_id, tb_user_user_id
)
VALUES(
    #{roleId}, #{userId}
)

mapper.update:
UPDATE tb_user_role
SET
    tb_role_id = #{roleId}
WHERE
    tb_user_user_id = #{userId}
